"use strict";

function createVNode() {
  var args = Array.from(arguments);
  var name = args.shift();
  var data = args.shift() || {};
  var children = data.children || (args.length === 1 && Array.isArray(args[0]) ? args[0] : args);

  return {
    name: name,
    data: data,
    children: children
  };
}

module.exports = createVNode;